//=== VC4InstPrinter.h -*- C++ -*- ==//
#ifndef LLVM_LIB_TARGET_VC4_INSTPRINTER_VCINSTPRINTER_H
#define LLVM_LIB_TARGET_VC4_INSTPRINTER_VCINSTPRINTER_H

#include "llvm/ADT/StringRef.h"
#include "llvm/MC/MCInstPrinter.h"

namespace llvm {

class VC4InstPrinter : public MCInstPrinter {
public:
  VC4InstPrinter(const MCAsmInfo &MAI, const MCInstrInfo &MII,
                 const MCRegisterInfo &MRI)
    : MCInstPrinter(MAI, MII, MRI) {}

  // Autogenerated by tblgen.
  void printInstruction(const MCInst *MI, raw_ostream &O);
  static const char *getRegisterName(unsigned RegNo);

  void printRegName(raw_ostream &OS, unsigned RegNo) const override;
  void printInst(const MCInst *MI, raw_ostream &O, StringRef Annot,
                 const MCSubtargetInfo &STI) override;

private:
  bool printAliasInstr(const MCInst *MI, raw_ostream &O);
  void printOperand(const MCInst *MI, unsigned OpNo, raw_ostream &O);
};

} // namespace llvm

#endif // !LLVM_LIB_TARGET_VC4_INSTPRINTER_VCINSTPRINTER_H

